import React, { useEffect, useState } from 'react';
import './App.css';
const App = () => {
	const appID = 'b6f20bc0';
	const api = '422420553f5e3163bb79c5f748041e2f';

	const req = `https://api.edamam.com/search?q=chicken&app_id=${appID}&app_key=${api}`;

	const [ counter, setCounter ] = useState(0);

	useEffect(() => {
		console.log('effect has been run');
	});

	return (
		<div className="App">
			<form className="search-form">
				<input className="search-bar" type="text" />
				<button onClick={() => setCounter(counter++)} className="search-btn" type="submit">
					{counter}
				</button>
			</form>
		</div>
	);
};

export default App;
